let seqs = [
  "Color_gradient",
  "MH_position",
  "MH_movement",
  "Content_visual",
  "Content_blend_mode",
  "Content_fx"
  ]

if(value == 1) {
  for (let s = 0; s < seqs.length; s++) {
    for (let i = 1; i <= 16; i++) {
      if(i == 1){
        set("Sequencer_" + seqs[s] + "_seq" + i, 1);
      }
      else {
        set("Sequencer_" + seqs[s] + "_seq" + i, 0);
      }
    }
  }
}

switch (value) {
  case "BPM":
    send("/Sequencer_Settings_beat_signal" , 0);
    break;
  case "madmom":
    send("/Sequencer_Settings_beat_signal" , 1);
    break;
  case "madmom BPM":
    send("/Sequencer_Settings_beat_signal" , 2);
    break;
  case "TD Kick":
    send("/Sequencer_Settings_beat_signal" , 3);
    break;
  default:
    send("/Sequencer_Settings_beat_signal" , 2);
}

